import React,{ Component } from 'react';
import Location from './Location';
import WeatherData from './WeatherData';
import{RAIN, CLOUD} from '../../constants/weathers';
import convert from 'convert-units';
//648008368e684007ca8939bd96942544
import './style.css';
const location="London,uk";
const api_key="648008368e684007ca8939bd96942544";
const api_weather =`https://
api.openweathermap.org/data/2.5/weather?q=${location},uk&appid=${api_key}`;
const data={
    temperature:26,
    weatherstate:RAIN,
    humidity:11,
    wind:'22m/s',

};
/*const data2={
    temperature:55,
    weatherstate:WINDY,
    humidity:15,
    wind:'55m/s',

};*/
class WeatherLocation extends Component{

    constructor()
    {
        super();
        this.state = {
            city:'Buenos Aires',
            data:data
        };

    }
    getWeatherState=(weather)=>{
        return CLOUD;
    }

    getTemp=kelvin=>{
        return convert(kelvin).from('K').to('C').toFixed(2); 
    }
    getData=weather_data=>{
        const {humidity,temp}=weather_data.main;
        const {speed}=weather_data.wind;
        const weatherstate=this.getWeatherState(this.weather);
        const temperature=this.getTemp(temp);
        const data={
            humidity,
            temperature,
            weatherstate,
            wind: `${speed} m/s`,

        }
        return data;
    }
    handleUpdateClick=()=>{
        fetch(api_weather).then(data=>{

            console.log(data);
            return data.json();
        }).then(weather_data=>{
            debugger;
            const data=this.getData(weather_data);
            this.setState({data});



        });
        console.log("Actualizado");
        /*
        this.setState({
            data:data2
        });*/
    }
    render=()=>{
        const {city,data}=this.state;
        return(
        <div className='weatherLocationCont'>
        <Location city={city}/>
        <WeatherData data={data}/>
        <button onClick={this.handleUpdateClick}>Actualizar</button>
        </div>
        );
    };
}
export default WeatherLocation;
