import React,{ Component } from 'react';
import Location from './Location';
import WeatherData from './WeatherData';
import{RAIN,CLOUDY} from '../../constants/weathers';

import './style.css';
const data={
    temperature:26,
    weatherstate:RAIN,
    humidity:11,
    wind:'22m/s',

};
const data2={
    temperature:55,
    weatherstate:CLOUDY,
    humidity:15,
    wind:'55m/s',

};
class WeatherLocation extends Component{

    constructor()
    {
        super();
        this.state = {
            city:'Buenos Aires',
            data:data
        };

    }

    handleUpdateClick=()=>{
        this.setState({
            city:'Buenos Aires',
            data:data2
        });
    }
    render=()=>{
        const {city,data}=this.state;
        return(
        <div className='weatherLocationCont'>
        <Location city={city}/>
        <WeatherData data={data}/>
        <button onClick={this.handleUpdateClick}>Actualizar</button>
        </div>
        );
    };
}
export default WeatherLocation;
